(77, 187, 'Kartlegging av arbeidsmiljø', 'arbeidsmiljo', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Vi</span><span style="color: black;"> har bred erfaring i arbeidsmiljøkartlegging, og har blant annet gjennomført omfattende kartlegging blant mer enn 15 000 ansatte i Helse Midt-Norge. Arbeidsmiljøkartleggingen vil være tverrfaglig fundert, og både psykolog, spesialist i arbeidsmedisin og fysioterapeut kan være bidragsytere.</span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">I fellesskap avklarer vi omfang og målgruppe, og avstemmer spørsmål som må tilpasses den enkelte virksomhet.  Selve undersøkelsen gjennomføres via vårt </span></span><span style="font-family: verdana,geneva; font-size: small; color: #7f9dc1; vertical-align: baseline; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><a href="http://checkware.com/" target="_blank">webbaserte</a><a href="http://checkware.com/" target="_blank"> verktøy</a> </span><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"><span style="font-family: verdana,geneva; font-size: small;">der hver enkelt respondent går inn og svarer på spørsmålene.  Erfaringsvis vet vi at oppfølgingsfasen i etterkant er meget viktig. Det er her tiltak skal etableres og medarbeiderne motiveres til å gjøre noe med sin egen arbeidssituasjon.</span> </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Det vil variere hvilken rolle vi i </span><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse har i denne fasen, og hvordan prosessoppfølgingen gjennomføres. Vi kan bistå med ulike oppfølgingstiltak, herunder <a href="index.php?option=com_content&amp;view=article&amp;id=89&amp;catid=87">konflikthåndtering</a>, <a href="index.php?option=com_content&amp;view=article&amp;id=86&amp;catid=87">lederveiledning og lederutvikling</a></span><span style="color: #000000;">, kurs for arbeidstagere i risikosonen for sykmelding, ulike sykefraværsreduserende tiltak og eventuelt individuell oppfølging av arbeidstakere som er i risikosonen for å bli sykmeldt. Ta kontakt med oss for å høre hvordan vi best kan hjelpe deg. <br /></span></span></p>', '', 1, 0, 0, 85, '2013-01-18 12:58:16', 661, '', '2013-02-04 09:05:11', 661, 0, '0000-00-00 00:00:00', '2013-01-18 12:58:16', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"0","link_titles":"0","show_intro":"","show_category":"","link_category":"0","show_parent_category":"0","link_parent_category":"0","show_author":"0","link_author":"0","show_create_date":"0","show_modify_date":"0","show_publish_date":"0","show_item_navigation":"0","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"0","show_hits":"0","show_noauth":"0","urls_position":"1","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 22, 0, 0, '', '', 1, 77, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(92, 205, 'Medarbeidersamtale', 'medarbeidersamtale', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Medarbeidersamtalen er et viktig redskap for både leder og medarbeider.  Medarbeidersamtaler skal først og fremst avklare gjensidige forventninger ved arbeidet, bygge tillit og åpenhet samt undersøke muligheten for medarbeidernes videre faglige utvikling. Mange ledere synes det er utfordrende å stille de rette spørsmålene som åpner opp for dette. Ikke bare er spørsmålene som stilles av betydning, men kanskje enda viktigere er måten de stilles på.</span><span style="font-size: small; font-family: verdana, geneva; color: black;">Våre psykologer har bred erfaring og god kunnskap om hvilke tilnærminger som øker sannsynligheten for åpen og konstruktiv kommunikasjon om utfordrende tema.</span></p>', '', 1, 0, 0, 87, '2013-01-18 13:26:14', 661, '', '2013-02-04 20:09:15', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:26:14', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, 4, '', '', 1, 20, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(78, 189, 'Utredningsarbeid', 'utredningsarbeid', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Vi bistår virksomheter i å undersøke og kartlegge utfordringer i arbeidsmiljøet, både i forhold til det fysiske- og psykososiale arbeidsmiljø. </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Hvilke spesifikke utredningsverktøy man benytter vil avhenge av problemområde og omfang, og kan eksempelvis innbefatte: </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">• <span style="color: black;">Helsesamtaler med ansatte og tilhørende rapportering med forslag til tiltak</span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">• <span style="color: black;">Psykososial kartlegging i form av dybdeintervju med ansatte, i kombinasjon med enkle psykososiale kartleggingsskjema, hvor både kvalitative data og kvantitative data til sammen danner grunnlaget for et faglig velbegrunnet løsningsforslag.</span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">• <span style="color: black;">Avdelings/enhets- baserte ergonomiske gjennomganger hvor man i motsetning til individuelle arbeidsplassvurderinger foretar en helhetsvurdering av ergonomiske forhold ved et arbeidssted.</span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">• <span style="color: black;">Faktaundersøkelser</span><span style="color: black;"> knyttet til påstander om mobbing/trakassering.</span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">• <span style="color: black;">Kartlegging med leder og medbestemmelsesapparat (verneombud/tillitsvalgte) hvor man med hjelp av vår veilednings- og rådgivningsmetodikk får frem en klarere definisjon og avgrensning av problemområde.</span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></div>\r\n<div style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 0.19in; text-indent: -0.19in; direction: ltr; unicode-bidi: embed; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">• <span style="color: black;">Yrkeshygieniske kartlegginger og/eller arbeidsmedisinske kartlegginger individuelt eller gruppevis. </span></span></div>', '', 1, 0, 0, 85, '2013-01-18 13:07:34', 661, '', '2013-02-04 09:26:53', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:07:34', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 8, 0, 4, '', '', 1, 41, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(79, 190, 'Risikovurderinger', 'risikovurdering', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse kan tilby et eget elektronisk kartleggingsverktøy for risikovurderinger. Dette er en web-basert løsning for risikovurdering, der hver enkelt enhetsleder mottar individuelt brukernavn, samt passord  for innlogging på webløsningen. Web-løsningen har mange tilpasningsmuligheter slik at man kan velge å </span><span style="color: black;">risikovurdere</span><span style="color: black;"> et eller flere tema ut fra hva som er aktuelt for den enkelte enhet, </span><span style="color: black;">f.eks</span><span style="color: black;"> ergonomiske forhold, organisatoriske forhold og/eller psykososiale forhold.</span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse vil kvalitetssikre den elektroniske besvarelsen ut fra vår kjennskap til både lovverk og enhetens samhandling med bedriftshelsetjenesten. Dette gjøres ved individuell veiledning basert på dataene fra analysen. Det vil utarbeides et ferdig utkast til handlingsplan med alle punkter som bør utbedres. Dette vil sikre en felles standard og vil være tidsbesparende. Data vil være tilgjengelig for sammenligning etterfølgende år for å avsløre tendenser/trender i HMS-arbeidet ved enheten.</span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">For de som ikke ønsker å benytte den Web- baserte løsningen kan vi foreta risikovurderinger/</span><span style="color: black;">RoS</span><span style="color: black;">-analyser med egnet fagperson tilstede i virksomhet sammen med leder og verneombud. </span></span></p>', '', 1, 0, 0, 85, '2013-01-18 13:08:33', 661, '', '2013-02-04 09:18:53', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:08:33', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"0","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, 2, '', '', 1, 31, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(139, 264, 'Roar Eriksen', 'ansatte-roar-eriksen', '', '<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">Roar Eriksen er utdannet psykolog, og har fordypning i klinisk arbeidspsykologi. Roar tilfredsstiller NAVs resultatoppnåelseskrav for <a href="index.php?option=com_content&amp;view=article&amp;id=123&amp;catid=84&amp;Itemid=474">Raskere tilbake</a>, og har</span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;"> god kompetanse innen håndtering av arbeidskonflikter, kommunikasjonskurs og lederveiledning.</span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;"><br /></span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">E-post: roare@coperio.no</span></p>', '', 1, 0, 0, 102, '2013-01-24 14:44:14', 661, '', '2013-02-12 11:01:09', 661, 0, '0000-00-00 00:00:00', '2013-01-24 14:44:14', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 7, 0, 14, '', '', 1, 46, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(80, 191, 'Vernerunder', 'vernerunder', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">En vernerunde består av en gjennomgang av hele eller bestemte deler av virksomheten. Det er arbeidsgiver som er ansvarlig for å gjennomføre en vernerunde, men ofte er det ansvarlig leder som har fått det delegerte ansvaret og vil være den som går vernerunder sammen med verneombudet.</span><span style="font-size: small; font-family: verdana, geneva; color: black;">En representant fra bedriftshelsetjenesten er også gjerne med på vernerunden da de kan avdekke problemstillinger man ikke selv ser. Under vernerunder benytter man sjekklister hvor man kan notere avvik. Hvor omfattende en slik sjekkliste er, og hvor ofte vernerunder må gjennomføres, er avhengig av virksomhetens størrelse, risikobilde og driftsområde. </span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Når vernerunden er gjennomført er det viktig at det blir laget en spesifisert tiltaksplan, med hvem som er ansvarlig for å utbedre avvikene med tidsfrister. </span><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse bistår gjerne din virksomhet med etablering av rutiner, sjekklister og gjennomføring av vernerunder.</span></span></p>', '', 1, 0, 0, 85, '2013-01-18 13:09:11', 661, '', '2013-02-04 13:54:33', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:09:11', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, 3, '', '', 1, 15, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(81, 192, 'HMS- revisjoner', 'kartlegging-hms-revisjoner', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">En HMS- revisjon er en kartlegging hvor man </span><span style="color: black;">ettergår</span><span style="color: black;"> virksomhetens helse- miljø- og sikkerhetsarbeid (HMS) regelmessig. </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Hensikten med en slik revisjon er å tilse at virksomheten arbeider systematisk med HMS ut ifra <a href="http://www.lovdata.no/all/hl-20050617-062.html" target="_blank">Arbeidsmiljøloven §3-1</a> og </span><span style="color: black;">forskriften</span><span style="color: black;"> om helse, miljø og sikkerhet, samt at den fungerer som forutsatt. </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Vi bistår gjerne virksomheten med en slik HMS-revisjon. </span></p>', '', 1, 0, 0, 85, '2013-01-18 13:10:00', 661, '', '2013-02-04 09:03:10', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:10:00', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 0, 2, '', '', 1, 23, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(82, 193, 'Kartlegging av psykososiale og organisatoriske forhold', 'psykososiale-organisatoriske-forhold', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">En stor og stadig økende del av det totale sykefraværet i arbeidslivet er<span style="font-size: small; font-family: verdana, geneva; color: black;"> knyttet til organisatoriske faktorer. Eksempler på dette kan være </span>ensformig arbeid, for stor arbeidsmengde eller sviktende innflytelse over egen arbeidssituasjon. Hvordan forholdene tilrettelegges på arbeidsplassen kan ha stor betydning for utviklingen av ulike former for helseskader.</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">Psykososiale og ergonomiske belastninger viser seg også i form av for eksempel økt tidspress, stress, konflikter eller samarbeidsproblemer. E<span style="font-size: small; font-family: verdana, geneva; color: black;">ndrede arbeidsoppgaver, ny teknologi eller større krav til effektivitet </span><span style="font-size: small; font-family: verdana, geneva; color: black;">er endringer som vil oppleves ulikt fra person til person. </span>Vi har lang erfaring i større organisatoriske <span style="font-size: small; font-family: verdana, geneva; color: black;">omstillingsprosesser og kan bidra som rådgivende part for å legge forholdene til rette slik at virksomheten får hentet ut de positive effektene ved endring i tillegg til å minimaliserer de negative trekkene, samtidig som arbeidstakernes individuelle behov blir ivaretatt.</span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Vi jobber etter de krav som er beskrevet i <a href="http://www.lovdata.no/all/hl-20050617-062.html" target="_blank">Arbeidsmiljøloven</a> hvor det i § 4-1, 4-2, 4-3, 4-4 poengteres særlig at både fysiske, psykososiale og organisatoriske forhold må ses i sammenheng. Våre psykologer, fysioterapeuter og HMS- rådgivere har høy kompetanse innen kartlegging av arbeidsmiljøfaktorer, samt forebygging og håndtering av risikofaktorer knyttet til det psykososiale arbeidsmiljøet. Vår bistand i disse prosessene vil være tverrfaglig fundert og vil totalt sett virke forbyggende på sykefravær.</span></p>', '', 1, 0, 0, 85, '2013-01-18 13:13:24', 661, '', '2013-02-04 09:05:44', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:13:24', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 7, 0, 1, '', '', 1, 18, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(83, 195, 'Kartlegging av yrkeshygieniske', 'yrkeshygiene', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"> </p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Yrkeshygieniske tiltak innebærer måling av arbeidsatmosfæren og  kartlegging av kjemikalier. Tørrhet, kløe, eksem, trøtthet, hodepine og slimirritasjon i øyne, nese og hals er noen av de vanligste symptomene knyttet til dårlig inneklima og inneluftforurensning. Vi vil vurderer personbelastning, bygning, interiør, prosesser og arbeid. Vurderingen i kombinasjon med utførelse av videre tiltak vil kunne bidra til å redusere forekomst av symptomer knyttet til inneklima.</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Vi samarbeider med</span><span style="color: black;"> yrkeshygienikere og verneingeniører som vil bistå ved målinger av både støy, lys, luft og fuktighet som avdekker tilhørende problematikk. Vi vil deretter komme med kostnadseffektive løsningsforslag.  </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">I kartleggingen av kjemikalier følger vi <a href="http://www.lovdata.no/cgi-wift/ldles?doc=/sf/sf/sf-20010430-0443.html" target="_blank">forskrift om vern mot eksponering for kjemikalier på arbeidsplassen</a> og bruker dette som en rettesnor for å oppfylle kravene fra myndighetene. Kjemikalier er en del av hverdagen på mange arbeidsplasser. Rengjøringsmidler, maling og desinfiserende midler er eksempler på kjemikalier som kan utgjøre helsefare.</span><span style="font-family: verdana, geneva; font-size: small;"> Under<span style="font-family: verdana, geneva; font-size: small;"> kartlegging og risikovurdering av kjemikalier på arbeidsplassen, vil vi benytte oss av et nettbasert verktøy som vil være tilgjengelig for deg og din bedrift. Verktøyet vil gi bedriften kjennskap til hvor farlig et gitt kjemikalie er og gi nyttig informasjon om hvordan man kan gjennomføre arbeidsoppgavene på en forsvarlig måte. Verktøyet gir også indikasjoner på hvilke forhåndsregler som bør tas ved bruk av kjemikalet, og om det finnes alternative kjemikalier som kan benyttes som er mindre helseskadelige.</span></span></p>', '', 1, 0, 0, 85, '2013-01-18 13:14:49', 661, '', '2013-02-04 09:05:56', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:14:49', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 8, 0, 0, '', '', 1, 14, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(84, 197, 'Rådgivning ved ombygg og nybygg', 'radgivning-ombygg-nybygg', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Coperio Bedriftshelse kan tilby både ergonomisk og yrkeshygienisk kompetanse ved ombygginger og nybygg. Det vil ytes rådgivning i forhold til momenter som blant annet dimensjonering av ventilasjon, materialvalg i forhold til gjeldende fysiske arbeidsmiljøkrav (f.eks. akustiske egenskaper), vindusflater og ergonomiske arbeidsplasser. </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Vår erfaring er at vi bør benyttes i tidlig planfasen slik at nødvendige løsninger kan drøftes og implementeres med lavest mulig byggekostnad.</span></p>', '', 1, 0, 0, 87, '2013-01-18 13:17:04', 661, '', '2013-02-04 20:30:07', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:17:04', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote"