(85, 198, 'Inkluderende arbeidsliv', 'ia', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Inkluderende arbeidslivs-avtalen (IA-avtalen) har som mål " <em>å forebygge og redusere sykefraværet, styrke jobbnærværet og bedre arbeidsmiljøet, samt hindre utstøting og frafall fra arbeidslivet.</em>" </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Coperio</span><span style="color: black;">Bedrifthelse</span><span style="color: black;"> har bred erfaring og kan bistå med både støtte og rådgivning i det systematiske IA-arbeidet.  Gjennom vår lange erfaring med sykefraværsarbeid, sykefraværsreduserende tiltak og systematisk tilrettelegging for at arbeidstakere skal komme raskere tilbake i jobb samt få en bedre arbeidssituasjon kan vi bidra til at din bedrift når sine mål om et inkluderende arbeidsliv. </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">CoperioSenteret har siden 2003 etter avtale med NAV hjulpet arbeidstakere raskere tilbake i jobb gjennom tiltaket </span><span style="text-decoration: underline;"><span style="color: black;">"Raskere Tilbake".</span></span><span style="color: black;"> Gjennom vår lange erfaring har vi opparbeidet oss særlig god kjennskap til mulighetene og virkemidlene innen NAV-systemet, herunder rutiner for oppfølging av sykmeldte, avholdelse av dialogmøter, direkte kontakt med saksbehandlere, refusjonsordninger for arbeidsgiver.</span> </span></p>', '', 1, 0, 0, 87, '2013-01-18 13:18:32', 661, '', '2013-02-04 19:50:56', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:18:32', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 5, 0, 11, '', '', 1, 9, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(153, 278, 'Jørn Hoklan', 'ansatte-jorn', '', '<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">Jørn er utdannet psykolog, sivilingeniør data, dr. scient medisinsk bildebehandling.</span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">Jørn har lang erfaring med individualterapi rettet mot affektfobier. </span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">Forskning og undervisning ved NTNU</span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;">Jørn har skrevet mange kronikker og artikler. </span></p>', '', 1, 0, 0, 102, '2013-01-25 14:46:22', 661, '', '2013-02-04 09:04:50', 661, 0, '0000-00-00 00:00:00', '2013-01-25 14:46:22', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 0, 1, '', '', 1, 6, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(86, 199, 'Lederveiledning', 'lederveiledning', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse skal fungere som en lederstøtte i arbeidet med å sikre god oppfølging, adekvat tilrettelegging og optimal forebygging av sykefravær i din bedrift. Lederstøtte kan for eksempel være aktuelt i forbindelse med utfordrende medarbeidersamtaler, rådgivning i vanskelige personalsaker, konflikter, ved utarbeidelse og i tilknytning til innføring av ulike tiltak i enheten. I tillegg til å være et individuelt tiltak rettet mot ledere er dette også et tiltak som kan gjennomføres i mindre grupper av ledere og verneombud.</span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Vi</span><span style="color: black;"> har omfattende erfaring med rådgivning og veiledning til ledere og mellomledere i forbindelse med oppfølging av sykefravær. Våre rådgivere er psykologer med høy kompetanse innen veiledning av voksne og lederutvikling.</span> </span><span style="font-size: small; font-family: verdana, geneva; color: black;">Vårt mål er å sette ledelsen i stand til å kunne mestre samtaler med sine ansatte på en optimal måte og vi kan tilby bistand ved oppfølging av sykefravær. Med hjelp av våre dyktige rådgivere vil du som leder tilegne deg nye og varige mestringsstrategier. </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></p>', '', 1, 0, 0, 87, '2013-01-18 13:19:20', 661, '', '2013-02-04 20:06:57', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:19:20', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, 10, '', '', 1, 14, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(87, 200, 'Arbeid og organisasjonsrådgivning', 'arbeid-og-organisasjonsradgivning', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="color: #000000; font-family: verdana, geneva; font-size: small;">Vi tilbyr rådgivning på organisasjonsplan. Dette innebærer en overordnet strategisk vurdering av virksomhetens mål, rutiner, arbeidsmetoder, tiltaksplaner og kommunikasjons prosesser.  Vurderingen utarbeides i tett samhandling med ledelse hvor formålet er å avdekke forbedringspotensial og øke virksomhetens effektivitet. Dette er et tiltak som kan gjøres på flere nivå og innen ulike enheter innen samme bedrift. Dette tiltaket passer godt i forlengelsen av en grundig arbeidsmiljøkartlegging.</span></p>', '', 1, 0, 0, 87, '2013-01-18 13:20:55', 661, '', '2013-02-04 08:53:59', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:20:55', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 0, 9, '', '', 1, 17, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(88, 201, 'Ergonomisk rådgivning', 'ergonomisk-radgivning', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Ergonomi er et tverrfaglig kunnskapsfelt som handler om tilpasning mellom arbeidsmiljø/teknikk og mennesket. For å unngå sykdom og belastningsskader, må både arbeidsgiver og arbeidstaker ta hensyn til hva som er god ergonomi.</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Våre </span><span style="color: black;">fysio</span><span style="color: black;">- og ergoterapeuter bistår gjerne i ergonomisk rådgivning.  Vi har god erfaring både med rådgivning en-til-en, og i større grupper. Ofte deltar vi på personalmøter eller i andre fora der ergonomi er på dagsorden. Ergonomisk rådgivning innebærer en totalvurdering av arbeidsplass i forhold til fysiske faktorer. Arbeidet gjennomføres av kvalifisert personell og inneholder blant annet ergonomi, analyse av inneklima og støy, gjennomgang av arbeidsrutiner, risikoanalyse og vurdering av risiko og helsefarer.</span></span></p>', '', 1, 0, 0, 87, '2013-01-18 13:21:53', 661, '', '2013-02-04 19:41:35', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:21:53', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_t