(166, 306, 'Gro Hellesnes', 'gro-hellesnes', '', '<p style="text-align: center;"><img src="images/ansatta/_MG_6960.jpg" border="0" width="150" height="225" style="vertical-align: top; display: block; margin-left: auto; margin-right: auto;" /><span style="font-size: x-small; font-family: verdana,geneva;"><strong><a href="index.php?option=com_content&amp;view=article&amp;id=141&amp;catid=102">   Gro Hellesnes   </a></strong></span><br /><span style="font-size: x-small;"><span style="font-family: verdana,geneva;"> Bedriftsrådgiver</span></span></p>', '', 1, 0, 0, 101, '2013-01-30 09:33:07', 660, '', '2013-02-04 21:34:25', 660, 0, '0000-00-00 00:00:00', '2013-01-30 09:33:07', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 12, 0, 4, '', '', 1, 3, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(97, 211, 'Dialogmøter', 'dialogmoter', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse har lang og bred erfaring med dialogsamtaler i virksomheter. I følge lovverket for sykefraværshåndtering (<a href="http://www.lovdata.no/all/hl-20050617-062.html" target="_blank">Arbeidsmiljøloven</a>) er arbeidsgiver lovpålagt å gjennomføre dialogsamtaler med  den sykemeldte arbeidstakeren. Formålet med dialogsamtalene er at partene sammen skal komme frem til tiltak som skal føre til at den sykemeldte kan vende tilbake til arbeid. </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;"> </span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Ifølge Arbeidsmiljøloven skal virksomheter som er tilknyttet  en godkjent bedriftshelsetjeneste innkalle bedriftshelsetjenesten til det første dialogmøtet. Bedriftshelsetjenestens rolle under dette møtet er å skape et godt samarbeidsklima mellom partene, og bidra aktivt med vår kjennskap til mulighetene i primærhelsetjenesten, spesialisthelsetjenesten og private helsetjenester, samt hvilke tiltak som kan være aktuelt å vurdere fra bedriftshelsetjenestens side. Når arbeidsgiver ivaretar sin omsorgsplikt på en god måte, samt at arbeidstaker på sin side er bevisst sin medvirkningsplikt, styrker det også nærværet i tillegg til å forebygge sykefravær. I tillegg til den første dialogsamtalen kan vi i </span><span style="color: black;">Coperio</span><span style="color: black;"> Bedriftshelse også bistå i andre dialogsamtaler etter behov dersom dette er ønskelig fra en av partene.<br /></span></span></p>', '', 1, 0, 0, 88, '2013-01-18 13:31:27', 661, '', '2013-02-04 20:47:25', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:31:27', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, 6, '', '', 1, 11, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(98, 212, 'Langtidssykmelding', 'langtidssykmelding', '', '<p style="language: nb-NO; margin-top: 0pt; margin-bottom: 0pt; text-align: center; direction: ltr; unicode-bidi: embed; vertical-align: baseline; mso-line-break-override: none; punctuation-wrap: hanging;"><span style="font-size: small; font-family: verdana, geneva; color: black; font-weight: bold;">Tilretteleggingsrådgivning</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Det kan være utfordrende å vurdere både timing, omfang og hvilken form et tilretteleggingstiltak bør ha for at det på en best mulig måte skal møte arbeidstakerens unike behov. Vi har lang erfaring og kompetanse i forhold til å gjøre denne type vurderinger, og vi kan bistå i alle faser av implementeringen av et tilretteleggingstiltak. </span></p>\r\n<p style="language: nb-NO; margin-top: 0pt; margin-bottom: 0pt; text-align: justify; text-justify: inter-ideograph; direction: ltr; unicode-bidi: embed; vertical-align: baseline; mso-line-break-override: none; punctuation-wrap: hanging;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="language: nb-NO; margin-top: 0pt; margin-bottom: 0pt; text-align: center; direction: ltr; unicode-bidi: embed; vertical-align: baseline; mso-line-break-override: none; punctuation-wrap: hanging;"><span style="font-size: small; font-family: verdana, geneva; color: black; font-weight: bold;">Funksjonsvurdering </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Jobbmestring er et vidt begrep som favner en rekke aspekter knyttet til arbeidsfunksjon, som for eksempel håndtering av stress og søvnproblematikk.  Vi er godt kjent med arbeidsmetodikken som benyttes hos <a href="http://www.nav.no/Arbeid/Inkluderende+arbeidsliv/NAV+Arbeidslivssenter" target="_blank">NAV Arbeidslivssenter</a>, og vi vil kunne fungere som et supplement til tiltak i deres regi, på individ- og systemnivå.  Vi kan tilby tverrfaglige arbeidsevnevurderinger med/bestående av psykolog, lege og fysioterapeut. Det vil da foretas en vurdering og avklaring av den langtidssykemeldtes behandlingspotensial og restarbeidsevne, og hvorvidt nedsettelse av arbeidsevne er av en varig eller midlertidig karakter. </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;"> </span></p>\r\n<p style="language: nb-NO; margin-top: 0pt; margin-bottom: 0pt; text-align: center; direction: ltr; unicode-bidi: embed; vertical-align: baseline; mso-line-break-override: none; punctuation-wrap: hanging;"><span style="font-size: small; font-family: verdana, geneva; color: black; font-weight: bold;">Videre oppfølging</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Vi tilbyr alle våre kunder muligheten til å benytte andre deler av </span><span style="color: black;">CoperioSenteret</span><span style="color: black;"> sitt helsetjenesteapparat ved behov for videre oppfølging som går utover det arbeidsgiveren ønsker å stå økonomisk ansvarlig for.  Dette kan være aktuelt for langtidssykemeldte som kan ha helsemessige utfordringer som tilsier at oppfølging innen et bedriftshelsetjenesteperspektiv ikke blir tilstrekkelig. Vi tilbyr videreformidling til "</span><span style="text-decoration: underline;"><span style="color: black;"><a href="index.php?option=com_content&amp;view=article&amp;id=123&amp;catid=84&amp;Itemid=474">Raskere tilbake"</a> </span></span><span style="color: black;">og </span><span style="text-decoration: underline;"><span style="color: black;"><a href="index.php?option=com_content&amp;view=article&amp;id=182&amp;catid=91">Coperio Rehabilitering</a> </span></span><span style="color: black;">i de tilfeller hvor slitenhet/utmattelse eller smerteproblematikk ligger til grunn for sykefraværet.</span></span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline;"><span style="font-size: small; font-family: verdana, geneva; color: black;"> </span></p>\r\n<p style="language: nb-NO; margin-top: 0pt; margin-bottom: 0pt; text-align: center; direction: ltr; unicode-bidi: embed; vertical-align: baseline; mso-line-break-override: none; punctuation-wrap: hanging;"><span style="font-size: small; font-family: verdana, geneva; color: black; font-weight: bold;">Attføringsvurdering</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Vi har omfattende erfaring med å vurdere hensiktsmessige tiltak i forhold til attføring. Vi kan gjøre dette ut fra en helhetlig vurdering av arbeidstakeren med spesielt fokus på helsemessig funksjonsevne, muligheter og begrensninger.  Vi kan bistå i med rådgivning og spesifikk utredning når dette er nødvendig. Både vår arbeidsmedisiner og våre psykologer vil bidra i denne prosessen med komplementær kunnskap som inkluderer somatiske- og psykologiske faktorer.</span></p>', '', 1, 0, 0, 88, '2013-01-18 13:32:19', 661, '', '2013-02-04 21:07:09', 660, 0, '0000-00-00 00:00:00', '2013-01-18 13:32:19', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 5, 0, 5, '', '', 1, 11, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
