(114, 228, 'Kurs for arbeidsmiljøutvalgets medlemmer', 'kurs-amu', '', '<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Arbeidsmiljøutvalget (AMU) er et besluttende og rådgivende organ som ut ifra arbeidsmiljølovgivningen skal jobbe for å bedre arbeidsmiljøet på en arbeidsplass. Alle virksomheter med mer en 50 ansatte skal etablere et arbeidsmiljøutvalg (AMU), dette gjelder også bedrifter med mellom 20-50 ansatte dersom en av partene krever dette. I et arbeidsmiljøutvalg (AMU) skal det være like mange representanter fra arbeidsgiversiden som arbeidstakersiden, deriblant verneombud. På dette kurset får du innsikt i følgende tema og problemstillinger;</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">-  Arbeidsmiljøloven</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">-  Hvem bør sitte i et arbeidsmiljøutvalg</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">-  Hvilken rolle har arbeidsmiljøutvalget i virksomheten</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">-  Hva kreves av et etablert arbeidsmiljøutvalg</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">-  Hva bør og skal tas opp i arbeidsmiljøutvalget</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">-  Hvilke problemstillinger kan arbeidsmiljøutvalget bli stilt ovenfor og hvordan skal man håndtere disse</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed;"><span style="font-size: small; font-family: verdana, geneva; color: black;">Alle våre kurs kan tilpasses din virksomhet og dine behov.</span></p>', '', 1, 0, 0, 82, '2013-01-18 13:55:21', 661, '', '2013-02-04 09:09:01', 661, 0, '0000-00-00 00:00:00', '2013-01-18 13:55:21', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 0, 6, '', '', 1, 21, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(116, 230, 'Utmattelsestilstander', 'utmattelsestilstander', '', '<p style="text-align: left;"><span style="color: black; font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Det kan føles tungt og ensomt å ikke ha energi til å gjennomføre oppgaver man før håndterte daglig. Årsaken til plutselige uforklarlige smerter, slapphet og energimangel kan ofte være sammensatt. Mange opplever også at man ved svekket mestringsevne stigmatiseres både i arbeid og privat.</span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-size: small; font-family: verdana, geneva; color: black;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">CoperioSenteret</span><span style="color: black;"> samarbeider aktivt med flere forskningsmiljø innen psykisk helse og utmattelsestilstander. Vi tar deg på alvor og kan tilby en kvalifisert utredning og individuelt tilpasset behandling for å hjelpe deg videre til en fornyet tro på egen mestring i hverdagen. </span></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p style="margin-top: 0pt; margin-bottom: 0pt; direction: ltr; unicode-bidi: embed; vertical-align: baseline;"><span style="font-size: small; font-family: verdana, geneva; color: black;">  </span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"> </span></p>', '', 1, 0, 0, 84, '2013-01-21 07:48:41', 660, '', '2013-02-04 09:26:38', 661, 0, '0000-00-00 00:00:00', '2013-01-21 07:48:41', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","url