(191, 335, 'Lasse Johansen', 'om-oss-lasse', '', '<p><span style="font-family: verdana, geneva; font-size: small;">Utdannet psykolog. Fordypning innen voksen klinisk arbeidspsykologi og voksen klinisk spesialisering. </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Gruppebehandling og konflikthåndtering mv. </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Interesse: lederutvikling, organisasjonsutvikling.</span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Telefon: + 47 <span>482 23 285| E-post: lhj@coperio.no</span></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;"><span> </span></span></p>', '', 1, 0, 0, 91, '2013-02-01 13:51:40', 661, '', '2013-02-05 13:17:43', 661, 664, '2013-02-21 11:42:14', '2013-02-01 13:51:40', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, 5, '', '', 1, 62, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(126, 242, 'Dine rettigheter', 'rettigheter-psykolog', '', '<p><span style="font-family: verdana, geneva; font-size: small;"><strong>Henvisning</strong></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;">Du trenger ingen henvisning for å få time hos oss. Ta kontakt på telefon  + 47 73 92 92 30 for timebestilling. Avbestilling må skje senest to virkedager (helg medregnes ikke) før timeavtalen. Ubenyttede timer eller timer som avbestilles for sent faktureres til full pris. Dette gjelder selv om det oppstår sykdom eller andre uforutsette hendelser.</span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"><strong>Avbestilling av time ved hjelp av SMS</strong></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;">Dersom du ønsker å avbestille din avtalte time ved CoperioSenteret avd. Trondheim kan du sende oss en tekstmelding om dette. Send en tekstmelding med følgende innhold til telefonnummer 99 42 00 92: "Avbestilling" + "ditt navn" + "behandlingsdato og klokkeslett"</span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"><strong style="color: #000000;">Refusjonsordning og frikort</strong></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;">Som privatpraktiserende helseforetak har vi dessverre ingen refusjonsordning for våre psykologtjenester.</span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"><strong> </strong></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"><strong>Lov om pasient – og brukerrettigheter</strong></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">CoperioSenteret</span><span style="color: black;"> er forpliktet til å forholde seg til gjeldende helselover. Pasientrettigheter og arbeidslivets lovverk og spilleregler skal ivaretas på best mulig måte.</span>Som pasient har du en rekke rettigheter når du benytter helsetjenester i Norge. <a href="http://www.lovdata.no/all/nl-19990702-063.html">For mer informasjon.</a></span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;"> </span></p>\r\n<p> </p>', '', 1, 0, 0, 79, '2013-01-21 08:41:47', 660, '', '2013-02-21 13:08:44', 661, 0, '0000-00-00 00:00:00', '2013-01-21 08:41:47', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"0","link_titles":"0","show_intro":"0","show_category":"0","link_category":"0","show_parent_category":"0","link_parent_category":"0","show_author":"0","link_author":"0","show_create_date":"0","show_modify_date":"0","show_publish_date":"0","show_item_navigation":"0","show_icons":"0","show_print_icon":"0","show_email_icon":"0","show_vote":"0","show_hits":"0","show_noauth":"0","urls_position":"1","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 40, 0, 3, '', '', 1, 182, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
