(130, 249, 'Kurs/Seminarer', 'kurs-og-seminarer', '', '<p><span style="font-family: verdana, geneva; font-size: small;">CoperioSenteret tilbyr en lang rekke kurs for både bedrifter og privatpersoner. I kalenderen under finner du aktuelle kurs vi avholder denne våren. Har du ideer til kurs du gjerne skulle sett i vår kalender? Vi hører gjerne fra deg!</span></p>\r\n<p><span style="font-family: verdana, geneva; font-size: small;">Se også vår kursoversikt <a href="index.php?option=com_content&amp;view=article&amp;id=73&amp;catid=82">her.</a></span></p>', '', 0, 0, 0, 93, '2013-01-21 13:20:57', 661, '', '2013-02-14 10:08:51', 660, 0, '0000-00-00 00:00:00', '2013-01-21 13:20:57', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"0","link_titles":"0","show_intro":"0","show_category":"0","link_category":"0","show_parent_category":"0","link_parent_category":"0","show_author":"0","link_author":"0","show_create_date":"0","show_modify_date":"0","show_publish_date":"0","show_item_navigation":"0","show_icons":"0","show_print_icon":"0","show_email_icon":"0","show_vote":"0","show_hits":"0","show_noauth":"0","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 23, 0, 0, '', '', 1, 150, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(132, 252, 'Tjenester - Rehabilitering', 'tjenester-rehabilitering', '', '<p> </p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small;"><span style="color: black;">Rehabiliteringen foregår over 10 uker og er inndelt i to bolker på 5 uker hver. Vårt fremste mål er å oppnå økt funksjonsnivå og økt livskvalitet gjennom et tverrfaglig, individuelt tilrettelagt program. Vi utarbeider også egne kursrekker i tillegg til dagtilbudet i forbindelse med tinnitus som trenger særlig tilpasset oppfølging. </span></span><span style="font-family: Calibri;"><span style="font-size: medium;"><span style="color: #000000;">Hos oss får du grunding innføring og trening i hvordan du kan redusere og mestre plagene knyttet til tinnitus/øresus. Flere spesialister vil hjelpe deg med dette i tillegg til å gi deg en informasjon om mekanismene bak tinnitus: Legespesialist i Øre-nese-hals, audiograf, psykolog/professor i psykologi og fysioterapeut/spesialist i psykomotorisk fysioterapi. Kurset går over 6 ettermiddager fordelt på 6 uker.</span></span></span></p>\r\n<p style="text-align: left;"><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;"> </span></p>\r\n<table style="color: #7f9dc1; font-family: Tahoma,Helvetica,Arial,sans-serif;" border="0" cellspacing="1">\r\n<tbody style="font-family: Tahoma,Helvetica,Arial,sans-serif; color: #7f9dc1;">\r\n<tr style="font-family: Tahoma,Helvetica,Arial,sans-serif; color: #7f9dc1;">\r\n<td style="font-family: Tahoma,Helvetica,Arial,sans-serif; color: #7f9dc1;">\r\n<h3 style="font-family: Tahoma,Helvetica,Arial,sans-serif; color: #7f9dc1;"><strong style="line-height: 1.3em;"><span style="font-family: verdana, geneva; font-size: small;">I rehabiliterings-programmet jobber vi med ulike tema fordelt på flere arbeidsformer:   </span></strong></h3>\r\n<p style="color: #000000;"><span style="color: #000000; font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Forelesning| </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Refleksjon i gruppe| </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Diskusjon| </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Bevegelse| </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Avspenning| </span><span style="font-family: verdana, geneva; font-size: small; line-height: 1.3em;">Individuelt tilpasset aktivitet</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<h3><strong><span style="font-family: verdana,geneva; font-size: small; color: #7f9dc1;">Uke 1-5</span></strong></h3>\r\n<p style="text-align: left;"><strong><span style="font-family: verdana,geneva; font-size: small;"><span style="font-size: small; font-family: verdana, geneva;"><strong>Hver mandag, onsdag og fredag kl. 10.00 - 15.00</strong></span></span></strong></p>\r\n<p style="text-align: left;"> </p>\r\n<p><span style="font-family: Calibri;"><span style="font-size: medium;"><span style="color: #000000;">Her blir du kjent med arbeidsmetoder, hvilke teorier vi jobber etter og hvilke muligheter behandlingen gir, men aller mest med deg selv; dine utfordringer og ditt potensiale.</span></span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<h3><span style="font-family: verdana,geneva; font-size: small;"><strong style="color: #7f9dc1;">Uke 6- 10</strong></span></h3>\r\n<p><span style="font-size: small; font-family: verdana, geneva;"><strong>Hver tirsdag og torsdag kl. 10.00 - 15.00</strong></span></p>\r\n<p> </p>\r\n<p><span style="font-family: Calibri;"><span style="font-size: medium;"><span style="color: #000000;">I andre halvdel av kurset blir du utfordret på å finne ut hvilke områder du ønsker å fordype deg i, både kroppslig og mentalt, slik at du i økende grad gjennom rehabiliteringen får trent på det som er viktigst for nettopp deg.</span></span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<div> </div>\r\n<p> </p>', '', 1, 0, 0, 92, '2013-01-22 07:44:38', 660, '', '2013-02-26 14:58:03', 660, 0, '0000-00-00 00:00:00', '2013-01-22 07:44:38', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 38, 0, 0, '', '', 1, 159, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
