(218, 364, 'Livsstil', 'livsstil', '', '<p><span style="font-family: verdana,geneva; font-size: small;">Coperio Bedriftshelse tilbyr livsstilskurs til alle som har til hensikt å bedre livskvaliteten, få en bedre forståelse og økt kunnskap om viktigheten av en sunn helse gjennom bedre kosthold og økt fysisk aktivitet. Dette kurset vil ta for seg tematikk som kosthold/ernæring; råd og tips i forhold til hva kroppen behøver. Fysisk aktivitet/forebygging og trening; veiledning og råd i forhold til aktiviteter og øvelser som hver enkelt deltaker kan gjennomføre. For at man skal være i stand til å endre sine livsstilsvaner vil det være viktig at man gjennom hele kurset har fokus på det psykologiske aspektet. Det vil si at man vil fokusere på motivasjon, endringsfokusert veiledning og mestringsstrategier.</span></p>', '', 1, 0, 0, 82, '2013-02-21 12:56:09', 664, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2013-02-21 12:56:09', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 0, 2, '', '', 1, 10, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(219, 365, 'Coaching for personalledere', 'coaching-for-personalledere', '', '<p><span style="font-family: verdana,geneva; font-size: small;"><em>For ledere med personalansvar</em></span></p>\r\n<p><span style="font-family: verdana,geneva; font-size: small;">Denne type kurs er spesielt rettet mot ledere i forhold til utvikling av et rammeverk for å erkjenne og arbeide med individuelt tilpassede utfordringer knyttet til utøvelsen av ledelse. Kursprogrammet gir en innføring i sentrale retninger innen anerkjent lederteori og presenterer ut fra dette de mest relevante dimensjonene som er knyttet til kompetent personalledelse.</span></p>', '', 1, 0, 0, 82, '2013-02-21 12:57:41', 664, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2013-02-21 12:57:41', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":null,"urlatext":"","targeta":"","urlb":null,"urlbtext":"","targetb":"","urlc":null,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 0, 1, '', '', 1, 16, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
